{
    "settings": {
        "number_of_shards": 3,
        "number_of_replicas": 1
    },
    "mappings": {
        "properties": {
            "author": {
                "type": "text"
            },
            "blogId": {
                "type": "long"
            },
            "category": {
                "type": "keyword"
            },
            "content": {
                "type": "text"
            },
            "createTime": {
                "type": "date",
                "format": "yyyy-MM-dd HH:mm:ss.SSS"
            },
            "serialNum": {
                "type": "keyword"
            },
            "status": {
                "type": "integer"
            },
            "title": {
                "type": "text",
                "analyzer": "ik_max_word",          // 指定分词器，默认standard，有search_analyzer时必须指定
                "search_analyzer": "ik_max_word"    // 查询时使用的分词器，二者最好保持一致
            },
            "updateTime": {
                "type": "date",
                "format": "yyyy-MM-dd HH:mm:ss.SSS"
            },
            "visits": {
                "type": "integer"
            }
        }
    }
}

{
    "query": {
        "term": {
            "name": {
                "value": "Tony"
                //"case_insensitive": true // 7.10.0+版本后支持忽略大小写
            }
        }
    }
}

{
    "query": {
        "bool": {
            // 有权重的
            "must": [
                {
                    "match": {
                        "author": "Jack"
                    }
                },
                {
                    "match": {
                        "title": "Spring Data ElasticSearch学习教程2"
                    }
                }
            ]
        }
    }
}

{
	"query":{
		"match_all":{
		}
	},
    "from": 0,  // (页码-1) * size
    "size": 4,
    "_source":["blogId","content"] // 结果仅显示这两个字段
}

{
    "query": {
        "match_all": {}
    },
    "from": 0, // (页码-1) * size
    "size": 4,
    "_source":["blogId","content"],     // 结果仅显示这两个字段
    "sort": {                           // 先排序再分页
        "blogId": {
            "order": "desc"
        }
    }
}

{
	"aggs":{                                //聚合操作
		"author_group":{                    //名称，随意起名
			"terms":{                       //分组
				"field":"author.keyword"    //分组字段
			}
		}
	},
    "size":0                                // 不显示source
}

{
	"aggs":{                                //聚合操作
		"visits_avg":{                    //名称，随意起名
			"avg":{                       //分组
				"field":"visits"        //分组字段
			}
		}
	},
    "size":0                                // 不显示source
}
